import React from 'react';
import './App.css';

function App() {

  const [showModal, setShowModal] = useState(false);
  const [extractCode, setExtractCode] = useState('');
  const [isError, setIsError] = useState(false);
  const [extractCodeInput, setExtractCodeInput] = useState('');

  const handleDownload = () => {
  setShowModal(true);
  setIsError(false);
  setExtractCodeInput('');
};

const handleExtractCodeSubmit = () => {
  if (extractCodeInput === '1234') {
    // 提取码正确，触发下载
    alert('提取码正确，开始下载！');
    window.location.href = '/download/你的文件.zip'; // 替换为实际下载链接
    setShowModal(false);
  } else {
    // 提取码错误
    setIsError(true);
    setExtractCodeInput('');
  }
};

// 4. 添加关闭弹窗函数：
const handleCloseModal = () => {
  setShowModal(false);
  setIsError(false);
  setExtractCodeInput('');
};

// 5. 添加输入框变化处理：
const handleInputChange = (e) => {
  setExtractCodeInput(e.target.value);
};

// 6. 添加回车键支持：
const handleKeyPress = (e) => {
  if (e.key === 'Enter') {
    handleExtractCodeSubmit();
  }
};

  return (

    <div className="App">
      {/* 主标题 */}
      <h1 className="main-title">欢迎下载小鱿鱼桌宠</h1>

      {/* 副标题 */}
      <p className="subtitle">让它陪你看喷喷视频吧！（目前只支持windows系统）</p>

      <button
         className="download-btn"
          onClick={() => window.location.href = `${process.env.PUBLIC_URL}/downloads/memesquid.exe`}
      >
        下载
      </button>

      {/* 图片和箭头部分 */}
      <div className="image-section">
        <div className="image-container">

          <img className="demo-image" src={`${process.env.PUBLIC_URL}/images/demosquid.png`} alt="picture" />
          <div className="arrow-container">
            {/*<div className="arrow"></div>*/}
            <span className="arrow-text">←长这样</span>
          </div>
        </div>
      </div>

      {/* 视频部分 */}
      <div className="video-section">
        <p className="video-description">
          对战中不同事件会触发不同表情包，demo视频效果：
        </p>

        <div className="video-container">
          <video 
            className="demo-video"
            controls
           // poster="/video-thumbnail.jpg" // 可选的视频封面
          >
            <source src={`${process.env.PUBLIC_URL}/images/video.mp4`}  />

          </video>

          <p className="video-tip">
            ↑ 点击视频右上角的全屏按钮可以全屏播放
          </p>
        </div>
      </div>

      {/* 页脚 */}
      <footer className="footer">
        <p>© 包浆小鱿鱼表情包桌宠</p>
      </footer>
    </div>


  );
}

export default App;
