import React from 'react';
import './App.css';

function App() {
  const handleDownload = () => {
    // 这里可以添加实际下载逻辑
    alert('下载功能已触发！请将你的文件放在public/download文件夹中，并更新此链接。');
    // 实际使用时可以替换为：window.location.href = '/download/xiaoyouyu.zip';
  };

  return (
    <div className="App">
      {/* 主标题 */}
      <h1 className="main-title">欢迎下载包浆小鱿鱼表情包桌宠</h1>

      {/* 副标题 */}
      <p className="subtitle">让它陪你看喷喷视频吧！</p>

      {/* 下载按钮 */}
      <button className="download-btn" onClick={handleDownload}>
        下载
      </button>

      {/* 图片和箭头部分 */}
      <div className="image-section">
        <div className="image-container">
          {/* 假设图片名为 xiaoyouyu-demo.png */}
          <img 
            src="/xiaoyouyu-demo.png" 
            alt="包浆小鱿鱼表情包桌宠预览" 
            className="demo-image"
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = "https://via.placeholder.com/300x200/4a90e2/ffffff?text=小鱿鱼预览图";
            }}
          />
          <div className="arrow-container">
            <div className="arrow"></div>
            <span className="arrow-text">长这样</span>
          </div>
        </div>
      </div>

      {/* 视频部分 */}
      <div className="video-section">
        <p className="video-description">
          对战中不同事件会触发不同表情包，demo视频效果：
        </p>

        <div className="video-container">
          {/* 假设视频名为 xiaoyouyu-demo.mp4 */}
          <video 
            className="demo-video"
            controls
            poster="/video-thumbnail.jpg" // 可选的视频封面
          >
            <source src="/xiaoyouyu-demo.mp4" type="video/mp4" />
            <source src="/xiaoyouyu-demo.webm" type="video/webm" />
            您的浏览器不支持视频播放，请使用现代浏览器查看。
          </video>

          <p className="video-tip">
            ↑ 点击视频右上角的全屏按钮可以全屏播放
          </p>
        </div>
      </div>

      {/* 页脚 */}
      <footer className="footer">
        <p>© 包浆小鱿鱼表情包桌宠</p>
      </footer>
    </div>
  );
}

export default App;
